INSERT INTO `homebrew` VALUES (1,'c0c6067d3a6f7112625886db075eded496a28cda079868c968dcbcd8fdaa94b2','2022-08-13 21:21:33','2022-08-13 21:21:42','[{\"id\":\"_meta\",\"name\":\"Walter\'s Pokemon\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/ninjask_pokemon_1.png\",\"name\":\"Ninjask\",\"team\":\"townsfolk\",\"ability\":\"You start knowing one good player and one good player\'s character in play.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/slurpuff_pokemon_1.png\",\"name\":\"Slurpuff\",\"team\":\"townsfolk\",\"ability\":\"Your good neighbors cannot die at night while they are sober and healthy\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/turtonator_pokemon_1.png\",\"name\":\"Turtonator\",\"team\":\"townsfolk\",\"ability\":\"Each night* choose a player: if they would die tonight, they do not and you die instead. You also learn 3 players, one of whom caused the death.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/overqwil_pokemon_1.png\",\"name\":\"Overqwil\",\"team\":\"townsfolk\",\"ability\":\"Each night choose a living player: if they are an Outsider, all players are healthy or sober, but not both, until dusk. [-1 or +1 Outsider]\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/sylveon_pokemon_1.png\",\"name\":\"Sylveon\",\"team\":\"townsfolk\",\"ability\":\"Each night choose a player: they are healthy and sober until dusk. You cannot be poisoned.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/espeon_pokemon_1.png\",\"name\":\"Espeon\",\"team\":\"townsfolk\",\"ability\":\"Once per game, during the day, privately tell the Storyteller up to 5 roles. The Storyteller will tell you how many of those roles are alive.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/gliscor_pokemon_1.png\",\"name\":\"Gliscor\",\"team\":\"townsfolk\",\"ability\":\"Each night choose 2 players (not yourself): you learn if either is evil. The first time you would be poisoned each game, instead you do not wake for your ability tonight. [+ the Mimikyu]\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/manaphy_pokemon_1.png\",\"name\":\"Manaphy\",\"team\":\"townsfolk\",\"ability\":\"Each night you may choose a player: you learn if they are the same alignment as the previously chosen player(if there is one). You may do this a total of 3 times.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/magneton_pokemon_1.png\",\"name\":\"Magneton\",\"team\":\"townsfolk\",\"ability\":\"Each night* choose a player and a character: you learn if the player is that character or if that character is in play. If you chose the demon player, you are drunk until dusk.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/bellossom_pokemon_1.png\",\"name\":\"Bellossom\",\"team\":\"townsfolk\",\"ability\":\"Each night learn how many alive players were poisoned or drunk tonight. You cannot be drunk.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/togekiss_pokemon_1.png\",\"name\":\"Togekiss\",\"team\":\"townsfolk\",\"ability\":\"Each night* learn how many of the recently executed player\'s neighbors are evil. If the executed player was evil you are poisoned tonight instead.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/aegislash_pokemon_1.png\",\"name\":\"Aegislash\",\"team\":\"townsfolk\",\"ability\":\"The first time you die at night, you don\'t and then choose a player: they die.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/armaldo_pokemon_1.png\",\"name\":\"Armaldo\",\"team\":\"townsfolk\",\"ability\":\"If the Demon kills you, you learn a player who is a minion, with a priority of living minions.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/spinda_pokemon_1.png\",\"name\":\"Spinda\",\"team\":\"outsider\",\"ability\":\"If you are executed and there are no dead evil players, 1 good player is poisoned, and 1 good player is drunk.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/maractus_pokemon_1.png\",\"name\":\"Maractus\",\"team\":\"outsider\",\"ability\":\"When you learn that you died, publicly choose a living player. If that player is good, your good neighbors are drunk.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/ambipom_pokemon_1.png\",\"name\":\"Ambipom\",\"team\":\"outsider\",\"ability\":\"Each night*, if either good living neighbor is poisoned, you die.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/mimikyu_pokemon_1.png\",\"name\":\"Mimikyu\",\"team\":\"outsider\",\"ability\":\"You might register as an Evil Demon. If you are \\\"mad\\\" about being an Outsider, 1-3 good players become drunk. The drunk players do not become sober when you die.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/gengar_pokemon_1.png\",\"name\":\"Gengar\",\"team\":\"outsider\",\"ability\":\"You believe you are a different Outsider. Each night a living player other than yourself is poisoned until dusk.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/weezing_pokemon_1.png\",\"name\":\"Weezing\",\"team\":\"minion\",\"ability\":\"Your good neighbors are poisoned. Each night* while 5 or more players live, you may choose to explode. If you do so the demon does not wake up tonight, kill yourself, and one of your alive good neighbors.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/toxicroak_pokemon_1.png\",\"name\":\"Toxicroak\",\"team\":\"minion\",\"ability\":\"Once per game at night choose a player: they are poisoned. The 1st time you die, you live but register as dead, and at night you choose a second player: that player is poisoned.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/gardevoir_pokemon_1.png\",\"name\":\"Gardevoir\",\"team\":\"minion\",\"ability\":\"Each night* choose two living players: all other night abilities (except the demon\'s) that target the first target the second until dusk, and vice versa.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/banette_pokemon_1.png\",\"name\":\"Banette\",\"team\":\"minion\",\"ability\":\"You have the ability of a not in play townsfolk. If the Demon dies while 5 or more players live, you become the Demon. [-1 Outsider]\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/houndoom_pokemon_1.png\",\"name\":\"Houndoom\",\"team\":\"demon\",\"ability\":\"Each night* choose a player: they die. After, one of the chosen player\'s neighbors might also die.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/drapion_pokemon_1.png\",\"name\":\"Drapion\",\"team\":\"demon\",\"ability\":\"Each night* choose a player: they die. Players you vote for are poisoned tonight and tomorrow day.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/umbreon_pokemon_1.png\",\"name\":\"Umbreon\",\"team\":\"demon\",\"ability\":\"Each night* choose a player: they die. Any ability that targets you at night might instead target one of your alive neighbors.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/salazzle_pokemon_1.png\",\"name\":\"Salazzle\",\"team\":\"demon\",\"ability\":\"Each night* choose a player: they die. If the chosen player was poisoned, repeat. [+ the Gengar]\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/alolanmarowak_pokemon_1.png\",\"name\":\"Alolan Marowak\",\"team\":\"traveller\",\"ability\":\"You are assigned a same-aligned player: after they die, your votes count twice.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/alolanraichu_pokemon_1.png\",\"name\":\"Alolan Raichu\",\"team\":\"traveller\",\"ability\":\"Each night* choose a player: they may die. If they die from this, they may choose another player to repeat this process.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/grookey_pokemon_1.png\",\"name\":\"Grookey\",\"team\":\"traveller\",\"ability\":\"Each night choose a player: if you are good, their ability cannot malfunction until dusk; if you are evil, they are poisoned until dusk.\"},{\"image\":\"https:\\/\\/www.bloodstar.xyz\\/p\\/walterofclifton\\/Pokemon_1\\/missingno_pokemon_1.png\",\"name\":\"Missingno\",\"team\":\"fabled\",\"ability\":\"You can allow custom images in Settings under the Grimoire tab.\"}]');
